mapper.select:
select *
from CONFIG_PARAM
where CONFIG_GROUP = #{configGroup}
and CONFIG_KEY = #{configKey}
order by CONFIG_GROUP, CONFIG_KEY

mapper.update:
update CONFIG_PARAM
set CONFIG_VALUE = #{configValue}, CONFIG_VALUE_TYPE = #{configValueType}, MODIFYBY = #{modifyBy}
where CONFIG_GROUP = #{configGroup}
and CONFIG_KEY = #{configKey}

mapper.delete:
delete from CONFIG_PARAM
where CONFIG_GROUP = #{configGroup}
and CONFIG_KEY = #{configKey}
