mapper.sql:
from TICKET_MAS
where 1=1

    and status = #{status}


    and trunc(createdate) >= to_date(#{dateFrom},'yyyy-MM-dd')


    and to_date(#{dateTo},'yyyy-MM-dd') >= trunc(createdate)


    and ticket_mas_id = #{ticketMasId}


    and cust_code =#{custCode}


    and CREATEBY = #{createBy}

mapper.select:
select * from TICKET_MAS
where CREATEBY = #{createBy}
order by TICKET_MAS_ID desc

SELECT tm.TICKET_MAS_ID,tm.STATUS FROM TICKET_MAS tm INNER JOIN TICKET_DET td ON tm.TICKET_MAS_ID = td.TICKET_MAS_ID WHERE td.SERVICE_ID = #{serviceNo}
AND tm.STATUS <> #{status}
ORDER BY tm.TICKET_MAS_ID DESC
